import React, { useState, useEffect } from 'react';
import  Card from "../Card/card";
import _ from "lodash";
import CardDetail from '../CardDetail/card_detail';
import { fetchForecast } from '../../api/weatherApi';

const Forecast = ({lat, lng, address}) => {
    const [forecast, setForecast] = useState([]);
    const [selectedCard, setSelectedCard] = useState({});

    useEffect(() => {
        const fetchData = async () => {
            const result = await fetchForecast(lat, lng);

            const mapped_days = _.mapValues(result, element => {
                return {...element, date_label: new Date(element.dt * 1000).getUTCDate()};
            });

            const weather_days = _.mapValues(_.groupBy(mapped_days, "date_label"));
            setForecast(weather_days);
            setSelectedCard(mapped_days[0]);
        };

        fetchData();
    }, [lat, lng]);

    const isToday = date => {
        const today = new Date().getUTCDate();
        return today === date;
    }

    const changeCard = card => {
        this.setState({
            selected_card: card
        });
    }

    return(
        <div className="row">
            <CardDetail selected_card={selectedCard} isToday={isToday(selectedCard.date_label)} address={address}/>
            <div className="col-9">
                <div className="row no-gutters">
                    {Object.keys(forecast).map((day, i) => {
                        return (
                            <div key={i} className="col">
                                <h3 className={ isToday(forecast[day][0].date_label)  ? 'text-primary' : 'text-dark'}>
                                    {
                                        isToday(forecast[day][0].date_label) ? 'Today' :
                                        new Intl.DateTimeFormat('en-GB', {
                                            weekday: 'short'
                                        })
                                        .format(new Date(forecast[day][0].dt_txt))
                                    }
                                </h3>
                                <h5 className="text-muted">{new Intl.DateTimeFormat('en-GB', {
                                    day: 'numeric',
                                    month: 'short',
                                    year: '2-digit'
                                }).format(new Date(forecast[day][0].dt_txt))}</h5>
                                <div className="accordion">
                                    {forecast[day].map((element, i) => {
                                        return(
                                            <Card key={i} data={element} onSelectCard={changeCard}/>
                                        );
                                    })}
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
}


export default Forecast;
