import React from "react";
import styled from 'styled-components';

const WeekdayText = styled.h3`
  color: #343a40;
  font-size: 1.75rem;
  margin-bottom: .5rem;
  font-family: inherit;
  font-weight: 500;
  line-height: 1.2;
`;

const DateText = styled.h5`
  color: #6c757d;
  font-size: 1.25rem;
  margin-bottom: .5rem;
  font-family: inherit;
  font-weight: 500;
  line-height: 1.2;
`;

const IMG_SRC = "http://openweathermap.org/img/w/";

const Card = ({date, data, onSelectCard, isToday}) => {

  const showDetail= () => {
    onSelectCard(data);
  }

  return (
    <div
      className="card rounded-0 text-center pointer"
      onClick={() => showDetail()}
    >
      <WeekdayText>
        { isToday(date) ?
          "Today" :

          new Intl.DateTimeFormat("en-GB", {
            weekday: "long",
          }).format(new Date(date))
        }
      </WeekdayText>
      <DateText>
        {new Intl.DateTimeFormat("en-GB", {
          day: "numeric",
          month: "short",
          year: "2-digit",
        }).format(new Date(date))}
      </DateText>
      <div className="card-header" id={data.dt + "heading"}>
        <div className="row">
          <div className="col">
            <img
              src={IMG_SRC + data.weather[0].icon + ".png"}
              alt=""
            />
          </div>
          <div className="col">
            <h1 className="text-temperature">
              {new Intl.NumberFormat("en-GB", {
                maximumFractionDigits: 0,
              }).format(data.main.temp)}
              Â°
            </h1>
          </div>
        </div>
        <p>{data.weather[0].main}</p>
        <p className="text-muted">
          {new Intl.DateTimeFormat("en-GB", {
            timeZone: "GMT",
            hour12: true,
            hour: "numeric",
            minute: "numeric",
          }).format(new Date(data.dt_txt))}
        </p>
      </div>
    </div>
  );
}

export default Card;
