import React from 'react';
import styled from 'styled-components';
import { format } from 'date-fns';

const IMG_SRC = "http://openweathermap.org/img/wn/";

const Container = styled.div`
  display:flex;
  justify-content: space-around;
  margin: 4rem 0;
`;

const WeatherImg = styled.img`
  width: 5rem;
  height: 5rem;
`;

const HourlyForecast = ({hourlyForecast}) => {
  return(
    <Container>
      {Object.keys(hourlyForecast).map((key, i) => {
        return (
          <div key={`${i}-hour`}>
            <p>{format(new Date(hourlyForecast[key].dt_txt), 'haaaa')}</p>
            <WeatherImg src={IMG_SRC + hourlyForecast[key].weather[0].icon + "@2x.png"} alt={hourlyForecast[key].weather[0].description}/>
            <p>{hourlyForecast[key].main.temp} Â°C</p>
          </div>

        );
      })}
    </Container>
  );
};

export default HourlyForecast;
