import React, { useState } from 'react';
import './App.css';
import Forecast from './components/Forecast/forecast';
import Searchbar from './components/Searchbar/searchbar';

const App = () => {
  const [lat, setLat] = useState(localStorage.getItem('lat') || null);
  const [lng, setLng] = useState(localStorage.getItem('lng') || null);
  const [address, setAddress] = useState(JSON.parse(localStorage.getItem('address')));

  const onSubmitSearchBar = ({lat, lng}, address) => {
    const currentCountry = (key, string) => localStorage.setItem(key, JSON.stringify(string));
    setLat(String(lat));
    setLng(String(lng));
    setAddress(String(address));
    currentCountry('lat', lat);
    currentCountry('lng', lng);
    currentCountry('address', address);
  };

  return (
    <div className="App">
      <div className="row">
        <div className="col-12 jumbotron">
          <h1>Weather Channel</h1>
          <h4 className="lead">Click on each box to see more weather details</h4>
          <p>
            <small>
              This weather channel was made by <a href="http://elohinaguevara.com" target="_blank" rel="noopener noreferrer">Elohina Guevara</a>
              <br/>
              You can check the repo <a href="https://github.com/Elohina/dublin-weather" target="_blank" rel="noopener noreferrer">here</a>
            </small>
          </p>
        </div>
      </div>
      {window.google && <Searchbar address={''} onSubmit={onSubmitSearchBar} />}
      <div className="container-fluid">
        { lat && lng &&
          <Forecast lat={lat} lng={lng} address={address} />
        }
      </div>
    </div>
  );
}

export default App;
