import React, { Fragment } from "react";
import Card from "../Card/Card";
import styled from 'styled-components';

const Forecast = ({ forecast, onChangeTime, isToday }) => { 
  return (
    <div className="row">
      {Object.keys(forecast).map((day, i) => {
        return (
          <div key={i} className="col">
            { !isToday(forecast[day][0].date_label) && 
              <Fragment>
              <h3 className="text-dark">
                { 
                  new Intl.DateTimeFormat("en-GB", {
                    weekday: "short",
                  }).format(new Date(forecast[day][0].dt_txt))
                }
              </h3>
              <h5 className="text-muted">
                {new Intl.DateTimeFormat("en-GB", {
                  day: "numeric",
                  month: "short",
                  year: "2-digit",
                }).format(new Date(forecast[day][0].dt_txt))}
              </h5>
              </Fragment>
            }
            { !isToday(forecast[day][0].date_label) &&
              <div className="accordion">
                {/* {forecast[day].map((element, i) => {
                  return (
                    <Card key={i} data={element} onSelectCard={onChangeTime} />
                  );
                })} */}
                <Card key={i} data={forecast[day][0]} onSelectCard={onChangeTime} />
              </div>
            }
          </div>
        );
      })}
    </div>
  );
};

export default Forecast;
