import React from "react";
import styled from 'styled-components';

const WeekdayText = styled.h4`
  color: #343a40;
  margin-bottom: .5rem;
  font-family: inherit;
  font-weight: 500;
  line-height: 1.2;
`;

const DateText = styled.h5`
  color: #6c757d;
  margin-bottom: .5rem;
  font-family: inherit;
  font-weight: 500;
  line-height: 1.2;
`;

const StyledCard = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
`;

const CloudsContainer = styled.p``;

const DescriptionText = styled.p`
  color: #343a40;
  margin-bottom: .5rem;
  font-family: inherit;
  line-height: 1.2;
`;

const IMG_SRC = "http://openweathermap.org/img/w/";

const Card = ({dateLabel, data, onSelectCard, isToday}) => {

  const showDetail= () => {
    onSelectCard(data);
  }

  return (
    <StyledCard onClick={() => showDetail()} id={data.dt + "heading"}>
      <div style={{width: '8rem'}}>
        <WeekdayText>
          { isToday(dateLabel) ?
            "Today" :
            new Intl.DateTimeFormat("en-GB", {
              weekday: "long",
            }).format(new Date(data.dt_txt))
          }
        </WeekdayText>
        <DateText>
          {new Intl.DateTimeFormat("en-GB", {
            day: "numeric",
            month: "short",
            year: "2-digit",
          }).format(new Date(data.dt_txt))}
        </DateText>
      </div>
      <div>
        <img
          src={IMG_SRC + data.weather[0].icon + ".png"}
          alt=""
        />
        <DescriptionText>{data.weather[0].main}</DescriptionText>
      </div>
      <CloudsContainer>
        <DescriptionText>{data.clouds.all}%</DescriptionText>
      </CloudsContainer>
      <div>
        <h2>
          {new Intl.NumberFormat("en-GB", {
            maximumFractionDigits: 0,
          }).format(data.main.temp_min)}
          °
        </h2>
        <h2>
          {new Intl.NumberFormat("en-GB", {
            maximumFractionDigits: 0,
          }).format(data.main.temp_max)}
          °
        </h2>
      </div>
      {/* <div>
        <p>
          {new Intl.DateTimeFormat("en-GB", {
            timeZone: "GMT",
            hour12: true,
            hour: "numeric",
            minute: "numeric",
          }).format(new Date(data.dt_txt))}
        </p>
      </div> */}
    </StyledCard>
  );
}

export default Card;
