import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Card from '../Card/Card';
import styled from 'styled-components';

const StyledForecast = styled.div`
  width: 50%;
  padding: 3em;

  @media(max-width: 75em) {
    width: 100%;
  }
`;

const Forecast = ({ forecast, onChangeTime, isToday }) => { 
  const [selectedCard, setSelectedCard] = useEffect(null);

  return (
    <StyledForecast>
      <h3>Week</h3>
      <h5>Click on each day to see the details</h5>
      { Object.keys(forecast).map((day, i) => {
        return (
          <Card
            key={i}
            data={forecast[day][0]}
            onSelectCard={onChangeTime}
            dateLabel={forecast[day][0].date_label}
            isToday={isToday}
          />
        );
      })}
    </StyledForecast>
  );
};

export default Forecast;

Forecast.propTypes = {
  forecast: PropTypes.object,
  onChangeTime: PropTypes.func,
  isToday: PropTypes.func
};
