import React from "react";
import styled from 'styled-components';

const WeekdayText = styled.h4`
  color: #343a40;
  margin-bottom: .5rem;
  font-weight: 500;
  line-height: 1.2;
`;

const DateText = styled.h5`
  color: #515454;
  margin-bottom: .5rem;
  font-family: inherit;
  font-weight: 500;
  line-height: 1.2;
`;

const StyledCard = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
  padding: 1.5rem;
  border-radius: 5px;
  cursor: pointer;

  :hover {
    background-color: rgba(255,255,255,0.24);
  }
`;

const CardColumn = styled.div`
  display: flex;
  flex-direction: column;
  width: '8rem';
  justify-content: center;
  align-items: center;
`;

const NumberText = styled.p`
  font-size: 2rem;
`;

const DescriptionText = styled.p`
  color: #343a40;
  margin-bottom: .5rem;
`;

const MinTemp = styled(NumberText)`
  color: #2e40c7;
`;

const MaxTemp = styled(NumberText)`
  color: #de5555;
`;

const IMG_SRC = "http://openweathermap.org/img/wn/";

const Card = ({dateLabel, data, onSelectCard, isToday}) => {

  const firstHour = data[0];

  const showDetail= () => {
    console.log(data)
    onSelectCard(data);
  }

  return (
    <StyledCard onClick={() => showDetail()} id={firstHour.dt + "heading"}>
      <CardColumn style={{alignItems: 'start'}}>
        <WeekdayText>
          { isToday(dateLabel) ?
            "Today" :
            new Intl.DateTimeFormat("en-GB", {
              weekday: "long",
            }).format(new Date(firstHour.dt_txt))
          }
        </WeekdayText>
        <DateText>
          {new Intl.DateTimeFormat("en-GB", {
            day: "numeric",
            month: "short",
            year: "2-digit",
          }).format(new Date(firstHour.dt_txt))}
        </DateText>
      </CardColumn>
      <CardColumn>
        <img
          src={IMG_SRC + firstHour.weather[0].icon + "@2x.png"}
          alt=""
        />
        <DescriptionText>{firstHour.weather[0].main}</DescriptionText>
      </CardColumn>
      <CardColumn>
        <NumberText>{firstHour.clouds.all}%</NumberText>
        <DescriptionText>Clouds</DescriptionText>
      </CardColumn>
      <CardColumn>
        <MinTemp>
          {firstHour.main.temp_min}°C
        </MinTemp>
        <MaxTemp>
          {firstHour.main.temp_max}°C
        </MaxTemp>
      </CardColumn>
      {/* <div>
        <p>
          {new Intl.DateTimeFormat("en-GB", {
            timeZone: "GMT",
            hour12: true,
            hour: "numeric",
            minute: "numeric",
          }).format(new Date(firstHour.dt_txt))}
        </p>
      </div> */}
    </StyledCard>
  );
}

export default Card;
