import React from "react";
import Card from "../Card/Card";

const Forecast = ({ forecast, onChangeTime, isToday }) => { 
  return (
    <div style={{width: '40%', padding: '3rem', backgroundColor: '#110d3c'}}>
      <h3>Week</h3>
      { Object.keys(forecast).map((day, i) => {
        return (
          <div key={i}>
            <Card key={i} data={forecast[day][0]} onSelectCard={onChangeTime} dateLabel={forecast[day][0].date_label} isToday={isToday}/>
          </div>
        );
      })}
    </div>
  );
};

export default Forecast;
