import React, { useState, useEffect, Fragment } from "react";
import _ from "lodash";
import CardDetail from "../CardDetail/CardDetail";
import { fetchForecast } from "../../api/weatherApi";
import styled from "styled-components";

import Forecast from "../Forecast/Forecast";
import Searchbar from '../Searchbar/Searchbar';

const Container = styled.div`
  display: flex;
  flex-flow: row wrap;
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
  width: 90%;
  justify-content: center;
  background-color: '#fff';
`;

const Weather = ({ lat, lng, address, onSubmitSearchBar }) => {
  const [forecast, setForecast] = useState([]);
  const [selectedCard, setSelectedCard] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetchForecast(lat, lng);

      const mapped_days = _.mapValues(result, (element) => {
        return {
          ...element,
          date_label: new Date(element.dt * 1000).getUTCDate(),
        };
      });

      const weather_days = _.mapValues(_.groupBy(mapped_days, "date_label"));
      setForecast(weather_days);
      setSelectedCard(mapped_days[0]);
    };

    fetchData();
  }, [lat, lng]);

  const isToday = (date) => {
    const today = new Date().getUTCDate();
    return today === date;
  };

  return (
    <Container>
      {window.google && <Searchbar address={""} onSubmit={onSubmitSearchBar} />}
      <CardDetail
        selected_card={selectedCard}
        isToday={isToday(selectedCard.date_label)}
        address={address}
      />
      <Forecast
        forecast={forecast}
        onChangeTime={(card)=>(setSelectedCard(card))}
        isToday={isToday}
      />
    </Container>

  );
};

export default Weather;
